package com.jiaoerzhang.platform.adapter.album;import android.content.Context;import android.support.annotation.Nullable;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.jiaoerzhang.platform.R;import com.jiaoerzhang.platform.entity.album.SearchEntity;import java.util.List;public class ModleTagAdapter extends BaseQuickAdapter<String, BaseViewHolder> {    private Context context;    public ModleTagAdapter(Context context, int layoutResId, @Nullable List<String> data) {        super(layoutResId, data);        this.context = context;    }    @Override    protected void convert(BaseViewHolder helper, final String item) {        helper.setText(R.id.searchName,item);    }}