package com.jiaoerzhang.platform.adapter.album;import android.content.Context;import android.support.annotation.Nullable;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.jiaoerzhang.platform.R;import com.jiaoerzhang.platform.entity.album.SearchEntity;import com.jiaoerzhang.platform.entity.user.ProblemEntity;import java.util.List;public class SearchHistoryAdapter extends BaseQuickAdapter<SearchEntity, BaseViewHolder> {    private Context context;    public SearchHistoryAdapter(Context context, int layoutResId, @Nullable List<SearchEntity> data) {        super(layoutResId, data);        this.context = context;    }    @Override    protected void convert(BaseViewHolder helper, final SearchEntity item) {        helper.setText(R.id.searchName,item.name);    }}