package com.jiaoerzhang.platform.adapter.album;import android.content.Context;import android.support.annotation.Nullable;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.jiaoerzhang.platform.R;import com.jiaoerzhang.platform.entity.album.SearchEntity;import com.jiaoerzhang.platform.entity.album.SearchHotEntity;import java.util.List;public class SearchHotAdapter extends BaseQuickAdapter<SearchHotEntity.Content, BaseViewHolder> {    private Context context;    public SearchHotAdapter(Context context, int layoutResId, @Nullable List<SearchHotEntity.Content> data) {        super(layoutResId, data);        this.context = context;    }    @Override    protected void convert(BaseViewHolder helper, final SearchHotEntity.Content item) {        helper.setText(R.id.searchName,item.getHotWords());    }}