package com.jiaoerzhang.platform.adapter.home;import android.content.Intent;import android.net.Uri;import android.support.annotation.Nullable;import android.view.View;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.facebook.drawee.view.SimpleDraweeView;import com.jiaoerzhang.platform.R;import com.jiaoerzhang.platform.app.App;import com.jiaoerzhang.platform.entity.home.HomeEntity;import com.jiaoerzhang.platform.entity.home.HomeTopEntity;import com.jiaoerzhang.platform.entity.home.ModelEntity;import com.jiaoerzhang.platform.ui.activity.search.SearchActivity;import com.jiaoerzhang.platform.ui.activity.search.SearchHistoryActivity;import java.util.List;import static com.blankj.utilcode.util.ActivityUtils.startActivity;public class HomeTopAdapter extends BaseQuickAdapter<ModelEntity.Content, BaseViewHolder> {    public HomeTopAdapter(int layoutResId, @Nullable List<ModelEntity.Content> data) {        super(layoutResId, data);    }    @Override    protected void convert(final BaseViewHolder helper, final ModelEntity.Content item) {        helper.setText(R.id.topNameTv,item.getModelName());        helper.setText(R.id.num,helper.getLayoutPosition()+1+"");        Uri uri = Uri.parse(item.getModelUrl());        SimpleDraweeView simpleDraweeView = helper.itemView.findViewById(R.id.topIcon);        simpleDraweeView.setImageURI(uri);        simpleDraweeView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(App.context,SearchHistoryActivity.class);                intent.putExtra("keyword", item.getModelName());                startActivity(intent);            }        });    }}