package com.jiaoerzhang.platform.adapter.user;import android.content.Context;import android.content.Intent;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.blankj.utilcode.util.ToastUtils;import com.jiaoerzhang.platform.R;import com.jiaoerzhang.platform.entity.user.RechargeCoinEntity;import com.jiaoerzhang.platform.entity.user.RechargeLogEntity;import com.jiaoerzhang.platform.ui.activity.user.RechargeActivity;import org.jetbrains.annotations.NotNull;import java.util.ArrayList;import java.util.List;/** * 精品专辑和普通专辑列表 */public class RechareItemAdapter extends RecyclerView.Adapter<RechareItemAdapter.MyViewHolder1> {    private Context context;    private List<RechargeLogEntity.Content> list = new ArrayList<>();    private LayoutInflater inflater;    public RechareItemAdapter(Context context, List<RechargeLogEntity.Content> all) {        this.context = context;        this.list = all;        inflater = LayoutInflater.from(context);    }    @NonNull    @Override    public MyViewHolder1 onCreateViewHolder(@NonNull ViewGroup viewGroup, int viewType) {        View view = inflater.inflate(R.layout.recharge_coin_item, viewGroup, false);        return new MyViewHolder1(view);    }    @Override    public void onBindViewHolder(@NonNull MyViewHolder1 myViewHolder1, final int i) {        myViewHolder1.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(context, RechargeActivity.class);                intent.putExtra("pos",i);                intent.putExtra("from","masapay");                context.startActivity(intent);            }        });        RechargeLogEntity.Content coinEntity = list.get(i);        myViewHolder1.price.setText("RS."+coinEntity.getOrderAmount());        myViewHolder1.coinDesc.setText(coinEntity.getNum()+" Lady Coin(LC)");        myViewHolder1.codeNum.setText(coinEntity.getOrderId());    }    @Override    public int getItemCount() {        return list.size();    }    @Override    public long getItemId(int position) {        return super.getItemId(position);    }    public void refreshData(@NotNull List<RechargeLogEntity.Content> content) {        if (content.size() == 0) {            ToastUtils.showShort(R.string.no_more_data);            return;        }        list.addAll(content);        notifyDataSetChanged();    }    class MyViewHolder1 extends RecyclerView.ViewHolder {        private TextView coinDesc;        private TextView codeNum;        private TextView price;        public MyViewHolder1(@NonNull View itemView) {            super(itemView);            coinDesc = itemView.findViewById(R.id.coin_desc);            codeNum = itemView.findViewById(R.id.codeNum);            price = itemView.findViewById(R.id.price);        }    }}