package com.jiaoerzhang.platform.entity.albumimport com.jiaoerzhang.platform.lib_net.network.BaseResponsedata class AlbumEntity(    val content: MutableList<Content>,    val empty: Boolean,    val first: Boolean,    val last: Boolean,    val number: Int,    val numberOfElements: Int,    val pageable: Pageable,    val size: Int,    val sort: Sort,    val totalElements: Int,    val totalPages: Int):BaseResponse<AlbumEntity>() {    data class Content(        val clarity: String,        val coverUrl: String,        val createTime: Int,        val id: Int,        val isShelves: Int,        val name: String,        val person: String,        val ucollectNum: String,        val style: String,        public var isNot: String,        val type: Int    )    data class Sort(        val empty: Boolean,        val sorted: Boolean,        val unsorted: Boolean    )    data class Pageable(        val offset: Int,        val pageNumber: Int,        val pageSize: Int,        val paged: Boolean,        val sort: Sort,        val unpaged: Boolean    ) {        data class Sort(            val empty: Boolean,            val sorted: Boolean,            val unsorted: Boolean        )    }}