package com.jiaoerzhang.platform.entity.albumimport com.jiaoerzhang.platform.lib_net.network.BaseResponsedata class StyleEntity(    val content: MutableList<Content>,    val empty: Boolean,    val first: Boolean,    val last: Boolean,    val number: Int,    val numberOfElements: Int,    val pageable: Pageable,    val size: Int,    val sort: Sort,    val totalElements: Int,    val totalPages: Int):BaseResponse<StyleEntity>() {    data class Sort(        val empty: Boolean,        val sorted: Boolean,        val unsorted: Boolean    )    data class Content(            val code: String,            val id: Int,            val isUse: Int,            var name: String    )    data class Pageable(        val offset: Int,        val pageNumber: Int,        val pageSize: Int,        val paged: Boolean,        val sort: Sort,        val unpaged: Boolean    ) {        data class Sort(            val empty: Boolean,            val sorted: Boolean,            val unsorted: Boolean        )    }}