package com.jiaoerzhang.platform.entity.homeimport com.jiaoerzhang.platform.lib_net.network.BaseResponsedata class ModelEntity(    val content: List<Content>,    val empty: Boolean,    val first: Boolean,    val last: Boolean,    val number: Int,    val numberOfElements: Int,    val pageable: Pageable,    val size: Int,    val sort: Sort,    val totalElements: Int,    val totalPages: Int):BaseResponse<ModelEntity>() {    data class Content(        val albumNum: Int,        val id: Int,        val modelName: String,        val modelUrl: String,        val modelingId: Int,        val showNum: Int,        val ucollectNum: Int    )    data class Sort(        val empty: Boolean,        val sorted: Boolean,        val unsorted: Boolean    )    data class Pageable(        val offset: Int,        val pageNumber: Int,        val pageSize: Int,        val paged: Boolean,        val sort: Sort,        val unpaged: Boolean    ) {        data class Sort(            val empty: Boolean,            val sorted: Boolean,            val unsorted: Boolean        )    }}