package com.jiaoerzhang.platform.ui.activity.albumimport android.os.Bundleimport android.support.v4.app.Fragmentimport com.bumptech.glide.load.model.StringLoaderimport com.jiaoerzhang.platform.Rimport com.jiaoerzhang.platform.entity.album.FavoriteTypeEntityimport com.jiaoerzhang.platform.entity.album.StyleEntityimport com.jiaoerzhang.platform.lib_core.base.BaseActivityimport com.jiaoerzhang.platform.ui.fragment.album.MyFrPagerAdapterimport com.jiaoerzhang.platform.ui.fragment.album.FavoriteAlbumContentimport com.jiaoerzhang.platform.widget.LoadingDialogimport kotlinx.android.synthetic.main.activity_collection_album.*class FavoriteAlbumActivity : BaseActivity() {    var loadingDialog: LoadingDialog? = null    override fun initData() {        val fragments: MutableList<Fragment> = ArrayList()        var tls: MutableList<FavoriteTypeEntity> = arrayListOf()            var favoriteTypeEntity:FavoriteTypeEntity = FavoriteTypeEntity()            favoriteTypeEntity.code = "all"        favoriteTypeEntity.name = "All"        var favoriteTypeEntity2:FavoriteTypeEntity = FavoriteTypeEntity()        favoriteTypeEntity2.code = "week"        favoriteTypeEntity2.name = "This Week"        var favoriteTypeEntity3:FavoriteTypeEntity = FavoriteTypeEntity()        favoriteTypeEntity3.code = "month"        favoriteTypeEntity3.name = "This Month"        tls.add(favoriteTypeEntity)        tls.add(favoriteTypeEntity2)        tls.add(favoriteTypeEntity3)        var stringlist:MutableList<String> = arrayListOf()        for (tl in tls) {            val bundle = Bundle()            bundle.putString("name", tl.name)            bundle.putString("code", tl.code)            fragments.add(FavoriteAlbumContent.getInstance(bundle))            stringlist.add(tl.name)        }        pager.adapter = MyFrPagerAdapter(getSupportFragmentManager(), stringlist as java.util.ArrayList<String>?, fragments)        tabs.setViewPager(pager)        pager.currentItem = 0    }    override fun initView() {        bar_back.setOnClickListener { this.finish() }        loadingDialog = LoadingDialog(this)    }    override fun bindLayoutId(): Int {        return R.layout.activity_collection_album    }}