package com.jiaoerzhang.platform.utilsimport android.net.Uriimport com.facebook.drawee.backends.pipeline.Frescoimport com.facebook.drawee.view.SimpleDraweeViewimport com.facebook.imagepipeline.postprocessors.IterativeBoxBlurPostProcessorimport com.facebook.imagepipeline.request.ImageRequestimport com.facebook.imagepipeline.request.ImageRequestBuilderclass FrescoUtil {    companion object {        /**         * 以高斯模糊显示。         *         * @param draweeView View。         * @param url        url.         * @param iterations 迭代次数，越大越魔化。         * @param blurRadius 模糊图半径，必须大于0，越大越模糊。         */        open fun showUrlBlur(draweeView: SimpleDraweeView, url: String, iterations: Int, blurRadius: Int) =                try {                    var uri: Uri = Uri.parse(url);                    var request: ImageRequest = ImageRequestBuilder.newBuilderWithSource(uri)                            .setPostprocessor(IterativeBoxBlurPostProcessor(6, blurRadius))                            .build()                    draweeView.setController(Fresco.newDraweeControllerBuilder()                            .setOldController(draweeView.controller)                            .setImageRequest(request)                            .build())                } catch (e: Exception) {                    e.printStackTrace()                }    }}